BASE <http://www.symbiote-h2020.eu/ontology/semanticmapping/testModel#>

	TRANSFORMATION reverse { 
		var o = [];
		for (var i = parameters[0].length - 1, j = 0; i >= 0; i--, j++)
			o[j] = parameters[0][i];
		o.join(''); 
	}

	RULE 
